def get_input
  puts "Que voulez vous faire/savoir?

  1 - Combien y a-t-il de journalistes dans ce array ?
  2 - Combien d'handle contiennent un numéro ?
  3 - Combien d'handle contiennent les 4 lettres du prénom 'Aude' à la suite (sans prendre en compte les majuscules) ?
  4 - Combien commencent par une majuscule (première lettre juste après le @) ?
  5 - Combien contiennent au moins une majuscule ?
  6 - Combien y a-t-il de underscore _ dans tous les pseudos confondus ?
  7 - Trie la liste de handle par ordre alphabétique.
  8 - Quels sont les 50 handles les plus courts de ce array ?
  9 - Quelle est la position dans l'array de la personne @epenser ?
  "

  print ">> "
  input = gets.to_i
  if  input >= 1 && input <= 9
    return input
  else
    puts "Veuillez choisir entre 1 et 9"
    get_input
  end
end

def quest_1 (tab)
  puts "Il y a #{tab.size} journalistes dans cet array."

end


def quest_2 (tab)
  (tab.size).times do |i|
    tab [i] = tab[i].scan(/\d+/).map(&:to_i)
  end
  tab.delete_if(&:empty?)
  puts "Il y a #{tab.size} handles qui contiennent un numéro"
end

def quest_3 (tab)
  tab.map!(&:downcase)
  (tab.size).times do |i|
    tab [i] = tab[i].scan(/aude/)
  end
  tab.delete_if(&:empty?)
  puts "Il y a #{tab.size} handles qui contiennent 'Aude'."
end

def quest_4 (tab)
  (tab.size).times do |i|
    tab [i] = tab[i][1].scan(/\p{Upper}/)
  end
  tab.delete_if(&:empty?)
  puts "Il y a #{tab.size} handles qui commencent par une majuscule."
end

def quest_5 (tab)
  (tab.size).times do |i|
    tab [i] = tab[i].scan(/\p{Upper}/)
  end
  tab.delete_if(&:empty?)
  puts "Il y a #{tab.size} handles qui commencent par une majuscule."
end

def quest_6 (tab)
  under = 0
  (tab.size).times do |i|
    if tab[i].count("_") >=1
      under += tab[i].count("_")
    end
  end
  puts "Il y a #{under} handles qui contiennent un underscore."
end

def quest_7 (tab)
  tab = tab.sort_by(&:upcase)
  puts "Voulez vous que je vous affiche le résultat? (y/n)"
  print ">> "
  if gets.chomp == "y"
    puts tab
  else
    return
  end
end

def quest_8 (tab)
  tab = tab.sort_by {|x| x.length}
  puts tab [0..49]
end

def quest_9 (tab)
  puts tab.index("@epenser")
end

def choose_quest (input, tab)
  if input == 1
    quest_1 (tab)
  elsif input == 2
    quest_2(tab)
  elsif input == 3
    quest_3(tab)
  elsif input == 4
    quest_4(tab)
  elsif input == 5
    quest_5(tab)
  elsif input == 6
    quest_6(tab)
  elsif input == 7
    quest_7(tab)
  elsif input == 8
    quest_8(tab)
  elsif input == 9
    quest_9(tab)
  end
end

def perform
  tab = fill_tab
  choose_quest(get_input, tab)
end

perform